var getIntersectionNode = function (headA, headB) {
  const set = new Set();
  let p = headA;
  while (p) {
    set.add(p);
    p = p.next;
  }
  p = headB;
  while (p) {
    if (set.has(p)) return p;
    p = p.next;
  }
  return null;
};

var getIntersectionNode = function (headA, headB) {
  let pA = headA;
  let pB = headB;
  if (!pA || !pB) return null;
  while (pA !== pB) {
    pA = pA === null ? headB : pA.next;
    pB = pB === null ? headA : pB.next;
  }
  return pA;
};
