class Solution {
   public:
    vector<int> singleNumbers(vector<int> &nums) {
        int val = 0;
        for (auto &num : nums) val ^= num;
        vector<int> ans(2, 0);
        int bit = 0;
        while ((val & 1 << bit) == 0) bit++;
        bit = 1 << bit;
        for (auto &num : nums) {
            if (num & bit)
                ans[0] ^= num;
            else
                ans[1] ^= num;
        }
        return ans;
    }
};
