class Solution {
   public:
    int singleNumber(vector<int>& nums) {
        int list[40] = {0};
        for (auto& num : nums) {
            for (int i = 0; i < 32; i++) {
                if (num & (1 << i)) list[i]++;
            }
        }
        int ans = 0;
        for (int i = 0; i < 32; i++) {
            if (list[i] % 3) {
                ans |= 1 << i;
            }
        }
        return ans;
    }
};
