class Solution {
   public:
    int subarraySum(vector<int>& nums, int k) {
        unordered_map<int, int> mmap;
        int sum = 0, ans = 0;
        mmap[0] = 1;
        for (auto& num : nums) {
            sum += num;
            if (mmap[sum - k]) ans += mmap[sum - k];
            mmap[sum]++;
        }
        return ans;
    }
};
