class Solution {
   public:
    int findMiddleIndex(vector<int>& nums) {
        int sum = 0;
        for (auto& num : nums) sum += num;
        int pre = 0;
        for (int i = 0; i < nums.size(); i++) {
            if (sum - nums[i] == pre) return i;
            pre += nums[i];
            sum -= nums[i];
        }
        return -1;
    }
};
