class Solution {
          public:
    int lengthOfLongestSubstring(string s) {
        int arr[200] = {0}, l = 0, r = 0, ans = 0, n = s.size();
        while (r < n) {
            while (r < n && arr[s[r]] < 1) arr[s[r++]]++;
            ans = max(ans, r - l);
            char ch = s[r++];
            arr[ch]++;
            while (s[l] != ch) arr[s[l++]]--;
            arr[s[l++]]--;
        }
        return ans;
    }
};
