class Solution {
   public:
    bool isPalindrome(string s) {
        if (s == " ") return 1;
        int l = 0, r = s.size() - 1;
        char lc, rc;
        while (l < r) {
            do {
                lc = tolower(s[l++]);
            } while (l < s.size() && !isdigit(lc) && !isalpha(lc));
            do {
                rc = tolower(s[r--]);
            } while (r >= 0 && !isdigit(rc) && !isalpha(rc));
            if (l < s.size() && r >= 0 && lc != rc) return 0;
        }
        return 1;
    }
};
