class Solution {
   public:
    bool check(string &s, int l, int r) {
        while (l < r) {
            if (s[l] != s[r]) return 0;
            l++;
            r--;
        }
        return 1;
    }
    bool validPalindrome(string s) {
        int l = 0, r = s.size() - 1;
        for (int l = 0, r = s.size() - 1; l < r; l++, r--) {
            if (s[l] == s[r])
                continue;
            else
                return check(s, l, r - 1) || check(s, l + 1, r);
        }
        return 1;
    }
};
