class Solution {
public:
    bool CheckPermutation(string s1, string s2) {
        if (s1.size() != s2.size()) return false;
        char list[130] = {0};
        for (auto &c : s1) list[c]++;
        for (auto &c : s2) if (list[c]-- == 0) return false;
        return true;
    }
};
