function findMagicIndex(nums: number[]): number {
  return _find(0, nums.length);
  function _find(l: number, r: number): number {
    if (l >= r) return -1;
    const mid = (l + r) >> 1;
    const num = nums[mid];
    if (num === mid) return mid;
    const left = _find(l, mid);
    if (left === -1) return _find(mid + 1, r);
    else return left;
  }
}
