class Solution:
    def pileBox(self, box: List[List[int]]) -> int:
        n = len(box)
        box.sort(key=lambda o: o[0] * o[1] * o[2], reverse=True)
        dp = [0] * n
        for i in range(n):
            dp[i] = box[i][2]
            for j in range(i - 1, -1, -1):
                if box[i][0] < box[j][0] and box[i][1] < box[j][1] and box[i][2] < box[j][2]:
                    dp[i] = max(dp[i], dp[j] + box[i][2])
        return max(dp)
