class Solution {
   public:
    int findClosest(vector<string>& words, string word1, string word2) {
        int p[2], ans = INT_MAX, n = words.size();
        p[0] = p[1] = -1;
        for (int i = 0; i < n; i++) {
            string word = words[i];
            int f = -1;
            if (word == word1) f = 0;
            if (word == word2) f = 1;
            if (f == -1) continue;
            p[f] = i;
            if (p[0] == -1 || p[1] == -1) continue;
            ans = min(ans, abs(p[0] - p[1]));
        }
        return ans;
    }
};
