function convertBiNode(root: TreeNode | null): TreeNode | null {
  if (root === null) return null;
  const q: TreeNode[] = [];
  inorder(root);
  for (let i = 0; i < q.length - 1; i++) {
    q[i].left = null;
    q[i].right = q[i + 1];
  }
  q[q.length - 1].left = q[q.length - 1].right = null;
  return q[0];
  function inorder(node: TreeNode | null): void {
    if (node === null) return;
    inorder(node.left);
    q.push(node);
    inorder(node.right);
  }
}
