function translateNum(num: number): number {
  if (num < 10) return 1;
  else if (num <= 25) {
    return 2;
  } else if (num < 100) {
    return 1;
  } else {
    const s = num.toString();
    const subNum2 = Number(s.substr(0, 2));
    let num2 = 0;
    if (subNum2 <= 25) num2 = translateNum(Number(s.substr(2)));
    return translateNum(Number(s.substr(1))) + num2;
  }
}
